import { combineReducers } from 'redux';
import tasksReducer from './tasksReducer';

export default combineReducers({
  tasks: tasksReducer,
  searchQuery: (state = '', action) => {
    switch (action.type) {
      case 'SET_SEARCH_QUERY':
        return action.payload;
      default:
        return state;
    }
  },
});
