import { ADD_TASK, MOVE_TASK, FILTER_TASKS } from './actions';

const initialState = {
  tasks: [],
  filterQuery: '',
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_TASK:
      return {
        ...state,
        tasks: [...state.tasks, { ...action.payload, id: Date.now().toString() }],
      };
    case MOVE_TASK:
      return {
        ...state,
        tasks: state.tasks.map(task =>
          task.id === action.payload.id ? { ...task, stage: action.payload.stage } : task
        ),
      };
    case FILTER_TASKS:
      return {
        ...state,
        filterQuery: action.payload,
      };
    default:
      return state;
  }
};

export default reducer;
