import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { moveTask } from '../redux/actions';
import Column from './Column';
import AddTaskForm from './AddTaskForm';
import SearchBar from './SearchBar';
import { Container, Grid } from '@mui/material';

const KanbanBoard = () => {
  const tasks = useSelector((state) => state.tasks);
  const searchQuery = useSelector((state) => state.searchQuery);
  const dispatch = useDispatch();

  const filteredTasks = tasks.filter((task) =>
    task.title.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const handleDrop = (id, newStatus) => {
    dispatch(moveTask(id, newStatus));
  };

  const columns = ['To Do', 'In Progress', 'Peer Review', 'Done'];

  return (
    <Container>
      <SearchBar />
      <Grid container spacing={2} justifyContent="center">
        {columns.map((column) => (
          <Grid item xs={12} sm={6} md={3} key={column}>
            <Column
              title={column}
              tasks={filteredTasks.filter((task) => task.status === column)}
              onDrop={handleDrop}
            />
          </Grid>
        ))}
      </Grid>
      <AddTaskForm />
    </Container>
  );
};

export default KanbanBoard;
