export const ADD_TASK = 'ADD_TASK';
export const MOVE_TASK = 'MOVE_TASK';
export const FILTER_TASKS = 'FILTER_TASKS';

export const addTask = (task) => ({
  type: ADD_TASK,
  payload: task,
});

export const moveTask = (id, stage) => ({
  type: MOVE_TASK,
  payload: { id, stage },
});

export const filterTasks = (query) => ({
  type: FILTER_TASKS,
  payload: query,
});
