import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { addTask } from '../redux/actions';
import { Fab, Modal, Box, TextField, Button } from '@mui/material';
import AddIcon from '@mui/icons-material/Add';

const AddTaskForm = () => {
  const [open, setOpen] = useState(false);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const dispatch = useDispatch();

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(addTask({ title, description, stage: 'To Do' }));
    setTitle('');
    setDescription('');
    setOpen(false);
  };

  return (
    <>
      <Fab color="primary" aria-label="add" onClick={() => setOpen(true)} style={{ position: 'fixed', bottom: '16px', right: '16px' }}>
        <AddIcon />
      </Fab>
      <Modal open={open} onClose={() => setOpen(false)}>
        <Box style={{ top: '50%', left: '50%', transform: 'translate(-50%, -50%)', position: 'absolute', width: '400px', backgroundColor: 'white', padding: '16px', boxShadow: 24 }}>
          <form onSubmit={handleSubmit}>
            <TextField
              label="Task Title"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              fullWidth
              required
              margin="normal"
            />
            <TextField
              label="Task Description"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              fullWidth
              required
              margin="normal"
              multiline
              rows={4}
            />
            <Button type="submit" variant="contained" color="primary" fullWidth> Add Task </Button>
          </form>
        </Box>
      </Modal>
    </>
  );
};

export default AddTaskForm;
