import React from 'react';
import { useSelector } from 'react-redux';
import { DndProvider } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import Column from './Column';

const KanbanBoard = () => {
  const tasks = useSelector((state) => state.tasks);

  const getColumnTasks = (column) => tasks.filter((task) => task.status === column);

  return (
    <DndProvider backend={HTML5Backend}>
      <div style={{ display: 'flex', justifyContent: 'space-between' }}>
        <Column title="To Do" tasks={getColumnTasks('To Do')} />
        <Column title="In Progress" tasks={getColumnTasks('In Progress')} />
        <Column title="Peer Review" tasks={getColumnTasks('Peer Review')} />
        <Column title="Done" tasks={getColumnTasks('Done')} />
      </div>
    </DndProvider>
  );
};

export default KanbanBoard;
